import React, {useState} from 'react';

import './ExpenseForm.css';

const ExpenseForm = () => {
    const [enteredTitle, setEnteredTitle] = useState('');
    const [enteredAmount, setEnteredAmount] = useState('');
    const [enteredDate, setEnteredDate] = useState('');

    const [userInput, setUserInput] = useState({
        enteredTitle: '',
        enteredAmount: '',
        enteredDate: ''
    });
    const titleChangeHandler = (event) => {
        setUserInput((prevSate) => {
            return {...prevSate, enteredTitle: event.target.value}
        });
    };

    const amountChangeHandler = (event) => {
        setUserInput((prevSate) => {
            return {...prevSate, enteredAmount: event.target.value}
        });
    };

    const dateChangeHandler = (event) => {
        setUserInput((prevSate) => {
            return {...prevSate, enteredDate: event.target.value}
        });
    };

    return <form>
        <div className="new-expense__controls">
            <div className="new-expense__control">
                <label>Title</label>
                <input type='text' onChange={titleChangeHandler}></input>
            </div>
            <div className="new-expense__control">
                <label>Amount</label>
                <input type='number' min="0.01" step="0.01"  onChange={amountChangeHandler}></input>
            </div>
            <div className="new-expense__control">
                <label>Date</label>
                <input type='date' min='2019-01-01' max='2022-12-31' onChange={dateChangeHandler}></input>
            </div>
            <div className="new-expense__actions">
                <button>Add Expense</button>
            </div>
        </div>
    </form>
};

export default ExpenseForm;
